function getCartesian(p, angle) {
    const f = getRad(angle)
    const posX = Math.round(p * Math.cos(f))
    const posY = Math.round(p * Math.sin(f))
    return {posX, posY}
}

function getRad(ang) {
    return (ang * Math.PI) / 180
}

// подумать, как рассчитывать, пока на глаз
const p = 150
const paddings = 2




export const mapCustomTree = (data) => {
    const level1 = data.children;
    const level2 = level1.map((item) => item.children);

    const lev1 = level1.flat().length
    const lev2 = level2.flat().length

    const steps = lev2 + lev1 * 2
    const angle1 = Math.round(360 / steps)
    const angle = 2 * Math.PI /360

    console.log('steps', steps)
    console.log('angle', angle)


    const groups = level1.reduce((acc, item) => {

        const elementsOnLevel = item.children.flat().length
        const itemsOnLevel = elementsOnLevel + paddings

        const nextCount = acc.prev + elementsOnLevel + 2

        const start = acc.prev * angle
        const end = itemsOnLevel * angle + start

        const center = ((end - start) / 2) + start

        const {posX, posY} = getCartesian(p, center)

        const startFirstChild = start + angle

        const children = item.children.map((el, index) => {
            let startChild = startFirstChild + index * angle
            const endChild = startChild + angle

            const center = ((endChild - startChild) / 2) + startChild
            const {posX, posY} = getCartesian(p*2, center)

            return {
                ...el,
                start: startChild,
                end: endChild,
                center,
                posX,
                posY
            }
        })

        const el = {
            ...item,
            children,
            start,
            end,
            center,
            posX,
            posY
        }

        return {
            prev: nextCount,
            list: [...acc.list, el]
        }
    }, {prev: 0, list: []})

    return groups.list
}
